import React, { useState, useEffect } from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from "./components/Items";
import Categories from "./components/Categories";
import ShowFullItem from "./components/ShowFullItem";
import BottomNavigation from "./components/BottomNavigation";
import AdvancedFilter from "./components/AdvancedFilter";

function App() {
  const [orders, setOrders] = useState([]);
  const [items] = useState([
    {
      id: 1,
      title: 'Труба сварная 50мм',
      desc: 'Сварная труба среднего диаметра',
      category: 'Сварные средние и малые',
      price: '100',
      material: 'steel',
      diameter: '50мм',
      wall: '3мм',
      gost: 'ГОСТ 10704-91',
      steelGrade: 'Ст3',
      warehouse: 'Москва',
      productType: 'Труба сварная'
    },
    {
      id: 2,
      title: 'Труба сварная 1020мм',
      desc: 'Сварная труба большого диаметра',
      category: 'Сварные большого диаметра',
      price: '200',
      material: 'steel',
      diameter: '1020мм',
      wall: '12мм',
      gost: 'ГОСТ 20295-85',
      steelGrade: 'Ст3',
      warehouse: 'Санкт-Петербург',
      productType: 'Труба сварная'
    },
    {
      id: 3,
      title: 'Труба горячедеформированная 89мм',
      desc: 'Горячедеформированная труба',
      category: 'Горячедеформированные',
      price: '150',
      material: 'steel',
      diameter: '89мм',
      wall: '4мм',
      gost: 'ГОСТ 8732-78',
      steelGrade: '20',
      warehouse: 'Москва',
      productType: 'Труба горячедеформированная'
    },
    {
      id: 4,
      title: 'Труба холоднодеформированная 32мм',
      desc: 'Холоднодеформированная труба',
      category: 'Холоднодеформированные',
      price: '180',
      material: 'steel',
      diameter: '32мм',
      wall: '2мм',
      gost: 'ГОСТ 8734-75',
      steelGrade: '10',
      warehouse: 'Екатеринбург',
      productType: 'Труба холоднодеформированная'
    },
    {
      id: 5,
      title: 'Труба профильная 40x40мм',
      desc: 'Профильная сварная труба',
      category: 'Профильные сварные',
      price: '220',
      material: 'steel',
      diameter: '40x40мм',
      wall: '2мм',
      gost: 'ГОСТ 8645-68',
      steelGrade: 'Ст3',
      warehouse: 'Москва',
      productType: 'Труба профильная'
    },
    {
      id: 6,
      title: 'Труба нержавеющая 57мм',
      desc: 'Труба из нержавеющей стали',
      category: 'Из нержавеющей стали',
      price: '350',
      material: 'stainless',
      diameter: '57мм',
      wall: '3мм',
      gost: 'ГОСТ 9941-81',
      steelGrade: '12Х18Н10Т',
      warehouse: 'Москва',
      productType: 'Труба нержавеющая'
    }
  ]);
  const [currentItems, setCurrentItems] = useState([]);
  const [showFullItem, setShowFullItem] = useState(false);
  const [fullItem, setFullItem] = useState({});
  const [searchQuery, setSearchQuery] = useState('');
  const [activeTab, setActiveTab] = useState('home');
  const [showAdvancedFilter, setShowAdvancedFilter] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [advancedFilters, setAdvancedFilters] = useState({
    warehouses: [],
    productTypes: [],
    diameters: [],
    walls: [],
    gosts: [],
    steelGrades: [],
    priceRange: { min: 0, max: 1000 }
  });

  useEffect(() => {
    setCurrentItems(items);
  }, [items]);

  // Фильтрация товаров
  useEffect(() => {
    let filtered = items;

    // Если выбрана категория главного фильтра
    if (selectedCategory) {
      filtered = filtered.filter(item => item.category === selectedCategory);
    }

    // Поиск по названию
    if (searchQuery) {
      filtered = filtered.filter(item => 
        item.title.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Расширенная фильтрация
    if (advancedFilters.warehouses.length > 0) {
      filtered = filtered.filter(item => 
        advancedFilters.warehouses.includes(item.warehouse)
      );
    }

    if (advancedFilters.productTypes.length > 0) {
      filtered = filtered.filter(item => 
        advancedFilters.productTypes.includes(item.productType)
      );
    }

    if (advancedFilters.diameters.length > 0) {
      filtered = filtered.filter(item => 
        advancedFilters.diameters.includes(item.diameter)
      );
    }

    if (advancedFilters.walls.length > 0) {
      filtered = filtered.filter(item => 
        advancedFilters.walls.includes(item.wall)
      );
    }

    if (advancedFilters.gosts.length > 0) {
      filtered = filtered.filter(item => 
        advancedFilters.gosts.includes(item.gost)
      );
    }

    if (advancedFilters.steelGrades.length > 0) {
      filtered = filtered.filter(item => 
        advancedFilters.steelGrades.includes(item.steelGrade)
      );
    }

    // Фильтр по цене
    filtered = filtered.filter(item => 
      item.price >= advancedFilters.priceRange.min && 
      item.price <= advancedFilters.priceRange.max
    );

    setCurrentItems(filtered);
  }, [searchQuery, advancedFilters, items, selectedCategory]);

  const onShowItem = (item) => {
    setFullItem(item);
    setShowFullItem(!showFullItem);
  };

  const chooseCategory = (category) => {
    if (category === 'all') {
      setSelectedCategory(null);
      setCurrentItems(items);
      return;
    }
    setSelectedCategory(category);
  };

  const deleteOrder = (id) => {
    setOrders(orders.filter(el => el.id !== id));
  };

  const addToOrder = (item) => {
    let isInArray = false;
    orders.forEach(el => {
      if(el.id === item.id)
        isInArray = true;
    });
    if(!isInArray)
      setOrders([...orders, item]);
  };

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  const handleAdvancedFilterChange = (newFilters) => {
    setAdvancedFilters(newFilters);
  };

  const toggleAdvancedFilter = () => {
    setShowAdvancedFilter(!showAdvancedFilter);
  };

  const renderContent = () => {
    switch (activeTab) {
      case 'home':
        return (
          <>
            {!selectedCategory ? (
              <Categories chooseCategory={chooseCategory}/>
            ) : (
              <>
                <div className="selected-category">
                  <h2>{selectedCategory}</h2>
                  <button 
                    onClick={() => setSelectedCategory(null)}
                    className="back-button"
                  >
                    ← Назад к категориям
                  </button>
                </div>
                <AdvancedFilter 
                  items={items}
                  onFilterChange={handleAdvancedFilterChange}
                  isOpen={showAdvancedFilter}
                />
                <Items onShowItem={onShowItem} items={currentItems} onAdd={addToOrder} />
              </>
            )}
          </>
        );
      case 'cart':
        return (
          <div className="cart-screen">
            <h2>Корзина</h2>
            {orders.length > 0 ? (
              <div className="orders-list">
                {orders.map(order => (
                  <div key={order.id} className="order-item">
                    <h3>{order.title}</h3>
                    <p>Цена: {order.price} руб.</p>
                    <button onClick={() => deleteOrder(order.id)}>Удалить</button>
                  </div>
                ))}
                <div className="total">
                  Итого: {orders.reduce((sum, order) => sum + parseInt(order.price), 0)} руб.
                </div>
              </div>
            ) : (
              <p>Корзина пуста</p>
            )}
          </div>
        );
      case 'account':
        return (
          <div className="account-screen">
            <h2>Личный кабинет</h2>
            <p>Добро пожаловать в ваш аккаунт</p>
            <div className="account-info">
              <p>Ваши заказы: {orders.length}</p>
              <p>Избранные товары: 0</p>
            </div>
          </div>
        );
      default:
        return (
          <>
            <Categories chooseCategory={chooseCategory}/>
            <AdvancedFilter 
              items={items}
              onFilterChange={handleAdvancedFilterChange}
              isOpen={showAdvancedFilter}
            />
            <Items onShowItem={onShowItem} items={currentItems} onAdd={addToOrder} />
          </>
        );
    }
  };

  return (
    <div className="wrapper">
      <Header 
        orders={orders} 
        onDelete={deleteOrder}
        onSearch={handleSearch}
        searchQuery={searchQuery}
        onToggleAdvancedFilter={toggleAdvancedFilter}
      />
      
      {renderContent()}
      
      {showFullItem && <ShowFullItem onAdd={addToOrder} onShowItem={onShowItem} item={fullItem}/>}
      
      <BottomNavigation 
        activeTab={activeTab}
        onTabChange={setActiveTab}
        cartItemsCount={orders.length}
      />
      
      <Footer />
    </div>
  );
}

