import React, { useState } from 'react'

function Item(props) {
    const [unitType, setUnitType] = useState('tons');
    const [quantity, setQuantity] = useState(1);

    const calculatePrice = () => {
        const basePrice = parseFloat(props.item.price);
        if (unitType === 'tons') {
            return (basePrice * quantity).toFixed(2);
        } else {
            // Предположим, что цена за метр = цена за тонну / 1000
            return (basePrice * quantity / 1000).toFixed(2);
        }
    };

    const handleAddToCart = () => {
        const itemWithQuantity = {
            ...props.item,
            quantity: quantity,
            unitType: unitType,
            totalPrice: calculatePrice()
        };
        props.onAdd(itemWithQuantity);
        setQuantity(1); // Сброс количества после добавления
    };

    return (
        <div className='item'>
            <h2 onClick={() => props.onShowItem(props.item)} alt={props.item.title}>{props.item.title}</h2>
            <p>{props.item.desc}</p>
            
            <div className="quantity-selector">
                <label>Единица измерения:</label>
                <select value={unitType} onChange={(e) => setUnitType(e.target.value)}>
                    <option value="tons">Тонны</option>
                    <option value="meters">Метры</option>
                </select>
                
                <label>Количество:</label>
                <input 
                    type="number" 
                    min="0.1" 
                    step="0.1"
                    value={quantity}
                    onChange={(e) => setQuantity(parseFloat(e.target.value) || 0.1)}
                />
            </div>
            
            <b>{calculatePrice()} руб.</b>
            <div className='add-to-card' onClick={handleAddToCart}>+</div>
        </div>
    );
}

export default Item





