// export default Categories
import React, { useState } from 'react'

function Categories(props) {
    const [categories] = useState([
        {
            key: 'all',
            name: 'Все'
        },
        {
            key: 'Ser',
            name: 'Простые'
        },
        {
            key: 'Orang',
            name: 'Винтаж'
        },
        {
            key: 'Stran',
            name: 'Необычные'
        }
    ]);

    return (
        <div className='categories'>
            {categories.map(el => (
                <div key={el.key} onClick={() => props.chooseCategory(el.key)}>{el.name}</div>
            ))}
        </div>
    )
}

export default Categories
