import React from 'react'

function Item(props) {
    return (
        <div className='item'>
            <h2 onClick={() => props.onShowItem(props.item)} alt={props.item.title}>{props.item.title}</h2>
            <p>{props.item.desc}</p>
            <b>{props.item.price} рубликов</b>
            <div className='add-to-card' onClick={() => props.onAdd(props.item)}>+</div>
        </div>
    );
}

export default Item
