import React from 'react'
import Order from './Order'
import { FaCreditCard } from "react-icons/fa";

function Cart(props) {
    const totalPrice = props.orders.reduce((sum, el) => {
        return sum + parseFloat(el.totalPrice || el.price);
    }, 0);

    const canProceedToPayment = () => {
        return props.userData && 
               props.userData.lastName && 
               props.userData.firstName && 
               props.userData.inn && 
               props.userData.phone && 
               props.userData.email;
    };

    const handlePayment = () => {
        if (!canProceedToPayment()) {
            alert("Вы должны заполнить данные аккаунта, чтобы перейти к оплате");
            return;
        }
        
        // Здесь будет логика перенаправления в банковское приложение
        // Временно просто показываем сообщение
        alert(`Перенаправление к оплате на сумму: ${totalPrice.toFixed(2)} руб.`);
        
        // В реальном приложении здесь будет что-то вроде:
        // window.location.href = `bank-app://payment?amount=${totalPrice}`;
    };

    return (
        <div className="cart">
            <h2>Корзина</h2>
            
            {props.orders.length === 0 ? (
                <div className="empty-cart">
                    <p>Корзина пуста</p>
                </div>
            ) : (
                <>
                    <div className="orders-list">
                        {props.orders.map(el => (
                            <Order 
                                key={el.id} 
                                item={el} 
                                onDelete={props.onDelete} 
                            />
                        ))}
                    </div>
                    
                    <div className="cart-summary">
                        <div className="total-price">
                            Итого: {totalPrice.toFixed(2)} руб.
                        </div>
                        
                        <button 
                            className="payment-btn"
                            onClick={handlePayment}
                        >
                            <FaCreditCard className="payment-icon" />
                            Перейти к оплате
                        </button>
                    </div>
                </>
            )}
        </div>
    );
}

export default Cart
