import React, { useState, useMemo } from 'react';

function AdvancedFilter({ items, onFilterChange }) {
  const [filters, setFilters] = useState({
    materials: [],
    diameters: [],
    lengths: [],
    priceRange: { min: 0, max: 1000 }
  });

  const [isExpanded, setIsExpanded] = useState(false);

  // Получаем уникальные значения для фильтров
  const filterOptions = useMemo(() => {
    const materials = [...new Set(items.map(item => item.material))];
    const diameters = [...new Set(items.map(item => item.diameter))];
    const lengths = [...new Set(items.map(item => item.length))];
    const prices = items.map(item => parseInt(item.price));
    const minPrice = Math.min(...prices);
    const maxPrice = Math.max(...prices);

    return { materials, diameters, lengths, minPrice, maxPrice };
  }, [items]);

  const handleMaterialChange = (material) => {
    const newMaterials = filters.materials.includes(material)
      ? filters.materials.filter(m => m !== material)
      : [...filters.materials, material];
    
    const newFilters = { ...filters, materials: newMaterials };
    setFilters(newFilters);
    onFilterChange(newFilters);
  };

  const handleDiameterChange = (diameter) => {
    const newDiameters = filters.diameters.includes(diameter)
      ? filters.diameters.filter(d => d !== diameter)
      : [...filters.diameters, diameter];
    
    const newFilters = { ...filters, diameters: newDiameters };
    setFilters(newFilters);
    onFilterChange(newFilters);
  };

  const handleLengthChange = (length) => {
    const newLengths = filters.lengths.includes(length)
      ? filters.lengths.filter(l => l !== length)
      : [...filters.lengths, length];
    
    const newFilters = { ...filters, lengths: newLengths };
    setFilters(newFilters);
    onFilterChange(newFilters);
  };

  const handlePriceChange = (min, max) => {
    const newFilters = { 
      ...filters, 
      priceRange: { min: parseInt(min), max: parseInt(max) } 
    };
    setFilters(newFilters);
    onFilterChange(newFilters);
  };

  const resetFilters = () => {
    const newFilters = {
      materials: [],
      diameters: [],
      lengths: [],
      priceRange: { min: 0, max: 1000 }
    };
    setFilters(newFilters);
    onFilterChange(newFilters);
  };

  return (
    <div className="advanced-filter">
      <div 
        className="filter-header"
        onClick={() => setIsExpanded(!isExpanded)}
      >
        <h3>Расширенный фильтр</h3>
        <span className={`expand-icon ${isExpanded ? 'expanded' : ''}`}>
          ▼
        </span>
      </div>

      {isExpanded && (
        <div className="filter-content">
          {/* Фильтр по материалу */}
          <div className="filter-group">
            <label>Материал:</label>
            <div className="filter-options">
              {filterOptions.materials.map(material => (
                <label key={material} className="filter-option">
                  <input
                    type="checkbox"
                    checked={filters.materials.includes(material)}
                    onChange={() => handleMaterialChange(material)}
                  />
                  {material}
                </label>
              ))}
            </div>
          </div>

          {/* Фильтр по диаметру */}
          <div className="filter-group">
            <label>Диаметр:</label>
            <div className="filter-options">
              {filterOptions.diameters.map(diameter => (
                <label key={diameter} className="filter-option">
                  <input
                    type="checkbox"
                    checked={filters.diameters.includes(diameter)}
                    onChange={() => handleDiameterChange(diameter)}
                  />
                  {diameter}
                </label>
              ))}
            </div>
          </div>

          {/* Фильтр по длине */}
          <div className="filter-group">
            <label>Длина:</label>
            <div className="filter-options">
              {filterOptions.lengths.map(length => (
                <label key={length} className="filter-option">
                  <input
                    type="checkbox"
                    checked={filters.lengths.includes(length)}
                    onChange={() => handleLengthChange(length)}
                  />
                  {length}
                </label>
              ))}
            </div>
          </div>

          {/* Фильтр по цене */}
          <div className="filter-group">
            <label>Цена: {filters.priceRange.min} - {filters.priceRange.max} руб.</label>
            <div className="price-range">
              <input
                type="range"
                min={filterOptions.minPrice}
                max={filterOptions.maxPrice}
                value={filters.priceRange.min}
                onChange={(e) => handlePriceChange(e.target.value, filters.priceRange.max)}
                className="price-slider"
              />
              <input
                type="range"
                min={filterOptions.minPrice}
                max={filterOptions.maxPrice}
                value={filters.priceRange.max}
                onChange={(e) => handlePriceChange(filters.priceRange.min, e.target.value)}
                className="price-slider"
              />
            </div>
          </div>

          <button onClick={resetFilters} className="reset-filters-btn">
            Сбросить фильтры
          </button>
        </div>
      )}
    </div>
  );
}

export default AdvancedFilter;
