// export default ShowFullItem
import React from 'react'

function ShowFullItem(props) {
    return (
        <div className='full-item'>
            <div>
                <img src={"./img/" + props.item.img} onClick={() => props.onShowItem(props.item)} alt={props.item.title} />
                <h2>{props.item.title}</h2>
                <p>{props.item.desc}</p>
                <b>{props.item.price} рубликов</b>
                <div className='add-to-card' onClick={() => props.onAdd(props.item)}>+</div>
            </div>
        </div>
    );
}

export default ShowFullItem
