import React, { useState, useEffect } from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from "./components/Items";
import Categories from "./components/Categories";
import ShowFullItem from "./components/ShowFullItem";
import BottomNavigation from "./components/BottomNavigation";
import AdvancedFilter from "./components/AdvancedFilter";

function App() {
  const [orders, setOrders] = useState([]);
  const [items] = useState([
    {
      id: 1,
      title: 'Труба 1',
      img: 'truba1.jpg',
      desc: 'Truba nomer 1.',
      category: 'Ser',
      price: '100',
      material: 'steel',
      diameter: '50mm',
      length: '6m'
    },
    {
      id: 2,
      title: 'Труба 2',
      img: 'truba2.jpg',
      desc: 'Truba nomer 2.',
      category: 'Orang',
      price: '200',
      material: 'copper',
      diameter: '32mm',
      length: '4m'
    },
    {
      id: 3,
      title: 'Труба 3',
      img: 'truba3.jpg',
      desc: 'Truba nomer 3.',
      category: 'Stran',
      price: '150',
      material: 'plastic',
      diameter: '40mm',
      length: '5m'
    },
    {
      id: 4,
      title: 'Труба 4',
      img: 'truba1.jpg',
      desc: 'Truba nomer 4.',
      category: 'Ser',
      price: '180',
      material: 'steel',
      diameter: '25mm',
      length: '6m'
    },
    {
      id: 5,
      title: 'Труба 5',
      img: 'truba2.jpg',
      desc: 'Truba nomer 5.',
      category: 'Orang',
      price: '220',
      material: 'copper',
      diameter: '50mm',
      length: '4m'
    }
  ]);
  const [currentItems, setCurrentItems] = useState([]);
  const [showFullItem, setShowFullItem] = useState(false);
  const [fullItem, setFullItem] = useState({});
  const [searchQuery, setSearchQuery] = useState('');
  const [activeTab, setActiveTab] = useState('home');
  const [advancedFilters, setAdvancedFilters] = useState({
    materials: [],
    diameters: [],
    lengths: [],
    priceRange: { min: 0, max: 1000 }
  });

  useEffect(() => {
    setCurrentItems(items);
  }, [items]);

  // Фильтрация товаров
  useEffect(() => {
    let filtered = items;

    // Поиск по названию
    if (searchQuery) {
      filtered = filtered.filter(item => 
        item.title.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Расширенная фильтрация
    if (advancedFilters.materials.length > 0) {
      filtered = filtered.filter(item => 
        advancedFilters.materials.includes(item.material)
      );
    }

    if (advancedFilters.diameters.length > 0) {
      filtered = filtered.filter(item => 
        advancedFilters.diameters.includes(item.diameter)
      );
    }

    if (advancedFilters.lengths.length > 0) {
      filtered = filtered.filter(item => 
        advancedFilters.lengths.includes(item.length)
      );
    }

    // Фильтр по цене
    filtered = filtered.filter(item => 
      item.price >= advancedFilters.priceRange.min && 
      item.price <= advancedFilters.priceRange.max
    );

    setCurrentItems(filtered);
  }, [searchQuery, advancedFilters, items]);

  const onShowItem = (item) => {
    setFullItem(item);
    setShowFullItem(!showFullItem);
  };

  const chooseCategory = (category) => {
    if (category === 'all') {
      setCurrentItems(items);
      return;
    }
    setCurrentItems(items.filter(el => el.category === category));
  };

  const deleteOrder = (id) => {
    setOrders(orders.filter(el => el.id !== id));
  };

  const addToOrder = (item) => {
    let isInArray = false;
    orders.forEach(el => {
      if(el.id === item.id)
        isInArray = true;
    });
    if(!isInArray)
      setOrders([...orders, item]);
  };

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  const handleAdvancedFilterChange = (newFilters) => {
    setAdvancedFilters(newFilters);
  };

  const renderContent = () => {
    switch (activeTab) {
      case 'home':
        return (
          <>
            <Categories chooseCategory={chooseCategory}/>
            <AdvancedFilter 
              items={items}
              onFilterChange={handleAdvancedFilterChange}
            />
            <Items onShowItem={onShowItem} items={currentItems} onAdd={addToOrder} />
          </>
        );
      case 'cart':
        return (
          <div className="cart-screen">
            <h2>Корзина</h2>
            {orders.length > 0 ? (
              <div className="orders-list">
                {orders.map(order => (
                  <div key={order.id} className="order-item">
                    <h3>{order.title}</h3>
                    <p>Цена: {order.price} руб.</p>
                    <button onClick={() => deleteOrder(order.id)}>Удалить</button>
                  </div>
                ))}
                <div className="total">
                  Итого: {orders.reduce((sum, order) => sum + parseInt(order.price), 0)} руб.
                </div>
              </div>
            ) : (
              <p>Корзина пуста</p>
            )}
          </div>
        );
      case 'account':
        return (
          <div className="account-screen">
            <h2>Личный кабинет</h2>
            <p>Добро пожаловать в ваш аккаунт</p>
            <div className="account-info">
              <p>Ваши заказы: {orders.length}</p>
              <p>Избранные товары: 0</p>
            </div>
          </div>
        );
      default:
        return (
          <>
            <Categories chooseCategory={chooseCategory}/>
            <AdvancedFilter 
              items={items}
              onFilterChange={handleAdvancedFilterChange}
            />
            <Items onShowItem={onShowItem} items={currentItems} onAdd={addToOrder} />
          </>
        );
    }
  };

  return (
    <div className="wrapper">
      <Header 
        orders={orders} 
        onDelete={deleteOrder}
        onSearch={handleSearch}
        searchQuery={searchQuery}
      />

      {renderContent()}

      {showFullItem && <ShowFullItem onAdd={addToOrder} onShowItem={onShowItem} item={fullItem}/>}

      <BottomNavigation 
        activeTab={activeTab}
        onTabChange={setActiveTab}
        cartItemsCount={orders.length}
      />

      <Footer />
    </div>
  );
}

export default App;




