import React from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from "./components/Items";
import Categories from "./components/Categories";
import ShowFullItem from "./components/ShowFullItem";

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      orders: [],
      currentItams: [],
      items: [
        {
          id: 1,
          title: 'Труба 1',
          img: 'truba1.jpg',
          desc: 'Truba nomer 1.',
          category: 'Ser',
          price: '100'
        },
        {
          id: 2,
          title: 'Труба 2',
          img: 'truba2.jpg',
          desc: 'Truba nomer 2.',
          category: 'Orang',
          price: '200'
        },
        {
          id: 3,
          title: 'Труба 3',
          img: 'truba3.jpg',
          desc: 'Truba nomer 3.',
          category: 'Stran',
          price: '150'
        }
      ],
      showFullItem: false
    }
    this.state.currentItams = this.state.items
    this.addToOrder = this.addToOrder.bind(this)
    this.deleteOrder = this.deleteOrder.bind(this)
    this.chooseCategory = this.chooseCategory.bind(this)
  }
  render() {
    return (
      <div className="wrapper">
        <Header orders={this.state.orders} onDelete={this.deleteOrder} />
        <Categories chooseCategory={this.chooseCategory}/>
        <Items items={this.state.currentItams} onAdd={this.addToOrder} />

        {this.state.showFullItem && <ShowFullItem />}
        <Footer />
      </div>
    );
  }

  onShowItem() {
    this.setState({showFullItem: !this.state.showFullItem})
  }

  chooseCategory(category) {
    if(category === 'all') {
      this.setStates({currentItems: this.state.items})
      return
    }
    this.setState({
      currentItams: this.state.items.filter(el => el.category === category)
    })
  }

  deleteOrder(id) {
    this.setState({orders: this.state.orders.filter(el => el.id !== id)})
  }

  addToOrder(item) {
    let isInArray = false
    this.state.orders.forEach(el => {
      if(el.id === item.id)
        isInArray = true
    })
    if(!isInArray)
      this.setState({orders: [...this.state.orders, item]})
  }
}

export default App;
