import React, { useState } from 'react'

function Categories(props) {
    const [categories] = useState([
        {
            key: 'all',
            name: '–í—Å–µ',
            icon: 'üì¶'
        },
        {
            key: '–°–≤–∞—Ä–Ω—ã–µ —Å—Ä–µ–¥–Ω–∏–µ –∏ –º–∞–ª—ã–µ',
            name: '–°–≤–∞—Ä–Ω—ã–µ —Å—Ä–µ–¥–Ω–∏–µ –∏ –º–∞–ª—ã–µ',
            icon: 'üîß'
        },
        {
            key: '–°–≤–∞—Ä–Ω—ã–µ –±–æ–ª—å—à–æ–≥–æ –¥–∏–∞–º–µ—Ç—Ä–∞',
            name: '–°–≤–∞—Ä–Ω—ã–µ –±–æ–ª—å—à–æ–≥–æ –¥–∏–∞–º–µ—Ç—Ä–∞',
            icon: 'üìè'
        },
        {
            key: '–ì–æ—Ä—è—á–µ–¥–µ—Ñ–æ—Ä–º–∏—Ä–æ–≤–∞–Ω–Ω—ã–µ',
            name: '–ì–æ—Ä—è—á–µ–¥–µ—Ñ–æ—Ä–º–∏—Ä–æ–≤–∞–Ω–Ω—ã–µ',
            icon: 'üî•'
        },
        {
            key: '–•–æ–ª–æ–¥–Ω–æ–¥–µ—Ñ–æ—Ä–º–∏—Ä–æ–≤–∞–Ω–Ω—ã–µ',
            name: '–•–æ–ª–æ–¥–Ω–æ–¥–µ—Ñ–æ—Ä–º–∏—Ä–æ–≤–∞–Ω–Ω—ã–µ',
            icon: '‚ùÑÔ∏è'
        },
        {
            key: '–ü—Ä–æ—Ñ–∏–ª—å–Ω—ã–µ —Å–≤–∞—Ä–Ω—ã–µ',
            name: '–ü—Ä–æ—Ñ–∏–ª—å–Ω—ã–µ —Å–≤–∞—Ä–Ω—ã–µ',
            icon: '‚¨õ'
        },
        {
            key: '–ò–∑ –Ω–µ—Ä–∂–∞–≤–µ—é—â–µ–π —Å—Ç–∞–ª–∏',
            name: '–ò–∑ –Ω–µ—Ä–∂–∞–≤–µ—é—â–µ–π —Å—Ç–∞–ª–∏',
            icon: '‚ú®'
        }
    ]);

    return (
        <div className='categories-grid'>
            {categories.map(el => (
                <div 
                    key={el.key} 
                    className='category-card'
                    onClick={() => props.chooseCategory(el.key)}
                >
                    <div className='category-icon'>{el.icon}</div>
                    <h3 className='category-name'>{el.name}</h3>
                </div>
            ))}
        </div>
    )
}

export default Categories








